mapper.select:
SELECT notification_id, user_id, notification_type, title, content, related_id, related_type, is_read, create_time, read_time FROM notification WHERE user_id = #{userId}
AND is_read = #{isRead}
AND notification_type = #{notificationType}
ORDER BY create_time DESC

mapper.update:
UPDATE notification
SET is_read = 1, read_time = NOW()
WHERE notification_id = #{notificationId}
