mapper.delete:
DELETE FROM student_course
WHERE student_id = #{studentId}
  AND course_id = #{courseId}

mapper.select:
SELECT id, student_id, course_id, teacher_id, join_method, join_time, create_time
FROM student_course
WHERE student_id = #{studentId}
  AND course_id = #{courseId}

SELECT COUNT(*)
FROM student_course
WHERE course_id = #{courseId}
