mapper.delete:
DELETE FROM teacher_course
WHERE teacher_id = #{teacherId}
  AND course_id = #{courseId}

mapper.select:
SELECT id, teacher_id, course_id, assign_time, invite_code, invite_code_expire_time
FROM teacher_course
WHERE teacher_id = #{teacherId}
  AND course_id = #{courseId}

SELECT COUNT(*)
FROM teacher_course
WHERE course_id = #{courseId}

SELECT teacher_id
FROM teacher_course
WHERE course_id = #{courseId}

mapper.update:
UPDATE teacher_course
SET invite_code = #{inviteCode}
WHERE teacher_id = #{teacherId}
  AND course_id = #{courseId}

mapper.select:
SELECT id, teacher_id, course_id, assign_time, invite_code, invite_code_expire_time
FROM teacher_course
WHERE invite_code = #{inviteCode}
